            "params": {
                "identity": self.identity,
                "payload": self.payload,
                "a": a,
                "b": b
            },
            "id": random.randint(1, 1000000)
        }
        json_request = json.dumps(request)
        self.sock.sendall(json_request.encode('utf-8'))
        return len(json_request)

    def recv(self) -> int:
        response_data = self.sock.recv(4096)
        response = json.loads(response_data.decode('utf-8'))
        if 'result' not in response:
            raise ValueError("Invalid response: missing 'result'")
        result = response['result']
        if not isinstance(result, bool):
            raise ValueError("Invalid result type")
        if result != self.expected:
            raise AssertionError(f"Validation failed: expected {self.expected}, got {result}")
        return 1 if result else 0

