class AnsweringMachineTCP:
    """
    An answering machine that use the classic socket.socket to
    answer multiple TCP clients
    """
    TYPE = socket.SOCK_STREAM

    def __init__(self, port=80, cls=conf.raw_layer, **kargs):
        self.port = port
        self.cls = cls
        self.optsniff = kargs

    def close(self):
        pass

