class SessionState:
    def __init__(self):
        self.seq_num = 0
        self.temp_key = None
        self.session_key = None
        self.hmac_key = None
        self.last_active = time.time()

def generate_nonce():
    return os.urandom(16)

def aes_encrypt(data, key):
    iv = get_random_bytes(16)
    cipher = AES.new(key, AES.MODE_CBC, iv)
    return iv + cipher.encrypt(data)

def aes_decrypt(data, key):
    iv = data[:16]
    cipher = AES.new(key, AES.MODE_CBC, iv)
    return cipher.decrypt(data[16:])

def compute_hmac(data, key):
    return hmac.new(key, data, hashlib.sha256).digest()

def format_message(msg_type, payload):
    header = struct.pack('<BII', msg_type, len(payload), 0)  # 0 is placeholder for HMAC length
    hmac_data = header + payload
    hmac = compute_hmac(hmac_data, session_state.hmac_key)
    return header + payload + hmac

def parse_message(data):
    if len(data) < 5:
        return None, None, None
    msg_type, payload_len, _ = struct.unpack('<BII', data[:5])
    payload = data[5:5+payload_len]
    hmac = data[5+payload_len:]
    if len(hmac) != 32:
        return None, None, None
    return msg_type, payload, hmac

def handle_sess_key_neg_start(payload, session_state):
    nonce = generate_nonce()
    temp_key = generate_nonce()
    hmac_data = nonce + temp_key
    hmac = compute_hmac(hmac_data, real_key)
    response = nonce + temp_key + hmac
    return format_message(1, response)

def handle_sess_key_neg_finish(payload, session_state):
    nonce, temp_key, hmac = payload[:16], payload[16:32], payload[32:]
    expected_hmac = compute_hmac(nonce + temp_key, real_key)
    if hmac != expected_hmac:
        return None
    session_key = aes_decrypt(temp_key, real_key)
    session_state.session_key = session_key
    session_state.hmac_key = generate_nonce()
    return format_message(2, b'')

def handle_dp_query_new(payload, session_state):
    mock_data = b"{'temperature': 25.3, 'humidity': 45}"
    encrypted = aes_encrypt(mock_data, session_state.session_key)
    hmac_data = encrypted + b'\x00' * 16  # Add dummy IV for HMAC
    hmac = compute_hmac(hmac_data, session_state.hmac_key)
    return format_message(3, encrypted + hmac)

