import socket
import multiprocessing
import os

class EventGeneric:
    def __init__(self, addr=None, port=None, name='events', shared_key=None, isDebug=False):
        self.name = name
        self.isDebug = isDebug
        self.shared_key = shared_key
        if port is None:
            self.port = get_free_port()
        else:
            self.port = port
        if addr is not None:
            self.addr, _ = addr
        else:
            self.addr = "0.0.0.0"
        self.file = f"./{self.name}.bin"

    def serve(self):
        if self.isDebug:
            self.logger = get_file_logger(self.name, level="DEBUG")
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        local_addr_port = (self.addr, self.port)
        sock.bind(local_addr_port)
        sock.listen(1)

        try:
            conn, addr = sock.accept()
            if self.isDebug:
                self.logger.debug(f"Open connection from {addr[0]}:{addr[1]}")
                event_file = open(self.file, "wb")
            try:
                while True:
                    data = conn.recv(1, socket.MSG_WAITALL)
                    if data:
                        event_file.write(data)
                    else:
                        break
                        raise KeyboardInterrupt
            except KeyboardInterrupt:
                pass
            finally:
                try:
                    if self.isDebug:
                        os.remove(self.file)
                except OSError:
                    pass
                conn.close()
                if self.isDebug:
                    self.logger.debug(f"Close connection from {addr[0]}:{addr[1]}")
            sock.close()
        except KeyboardInterrupt:
            pass
        except BrokenPipeError:
            pass
        finally:
            sock.close()
            if self.isDebug:
                self.logger.debug(f"Closed listen on {self.addr}:{self.port}")

    @staticmethod
    def spawn(addr=None, port=None, name='events', shared_key=None, isDebug=False):
        event = EventGeneric(addr, port, name, shared_key, isDebug)
        p = multiprocessing.Process(target=event.serve)
        p.start()
        return event.port, p

import socket
import select
import time
import types
import collections
import errno
import sys
import traceback

class Listener:
    """A socket wrapper object for listening sockets."""

    def __init__(self, host, port):
        """Create a listening socket on the given hostname and port."""
        self._closed = False
        self.host = host
        self.port = port
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self.sock.bind((host, port))
        self.sock.listen(5)

    def accept(self):
        """An event that waits for a connection on the listening socket.
        When a connection is made, the event returns a Connection
        object.
        """
        if self._closed:
            raise SocketClosedError()
        return AcceptEvent(self)

    def close(self):
        """Immediately close the listening socket. (Not an event.)"""
        self._closed = True
        self.sock.close()

class Connection:
    """A socket wrapper object for connected sockets."""

    def __init__(self, sock, addr):
        self.sock = sock
        self.addr = addr
        self._buf = b""
        self._closed = False

    def close(self):
        """Close the connection."""
        self._closed = True
        self.sock.close()

    def recv(self, size):
        """Read at most size bytes of data from the socket."""
        if self._closed:
            raise SocketClosedError()

        if self._buf:
            out = self._buf[:size]
            self._buf = self._buf[size:]
            return ValueEvent(out)
        else:
            return ReceiveEvent(self, size)

    def send(self, data):
        """Sends data on the socket, returning the number of bytes
        successfully sent.
        """
        if self._closed:
            raise SocketClosedError()
        return SendEvent(self, data)

    def sendall(self, data):
        """Send all of data on the socket."""
        if self._closed:
            raise SocketClosedError()
        return SendEvent(self, data, True)

    def readline(self, terminator=b"\n", bufsize=1024):
        """Reads a line (delimited by terminator) from the socket."""
        if self._closed:
            raise SocketClosedError()

        while True:
            if terminator in self._buf:
                line, self._buf = self._buf.split(terminator, 1)
                line += terminator
                yield ReturnEvent(line)
                break
            data = yield ReceiveEvent(self, bufsize)
            if data:
                self._buf += data
            else:
                line = self._buf
                self._buf = b""
                yield ReturnEvent(line)
                break

class AcceptEvent(WaitableEvent):
    """An event for Listener objects (listening sockets) that suspends
    execution until the socket gets a connection.
    """

    def __init__(self, listener):
        self.listener = listener

    def waitables(self):
        return (self.listener.sock,), (), ()

    def fire(self):
        sock, addr = self.listener.sock.accept()
        return Connection(sock, addr)

class ReceiveEvent(WaitableEvent):
    """An event for Connection objects (connected sockets) for
