class ParameterServer:
    def __init__(self, buffer_dir: str, drain_dir: str, capacity: int, force_sequential: bool) -> None:
        self.state = State(
            buffer_dir=buffer_dir,
            drain_dir=drain_dir,
            capacity=capacity,
            force_sequential=force_sequential,
            dir_lock=threading.Lock(),
            param_lock=threading.Lock(),
            buffer_size=0,
            submission_id=0,
            mortal_param=None,
            dqn_param=None,
            param_version=0,
            idle_param_version=0
        )

    def start_server(self, host: str, port: int) -> None:
        server = socketserver.ThreadingTCPServer((host, port), self.RequestHandler)
        print(f"Server started on {host}:{port}")
        server.serve_forever()

    class RequestHandler(socketserver.BaseRequestHandler):
        def handle(self):
            data = self.request.recv(1024)
            if not data:
                return
            msg = json.loads(data.decode('utf-8'))
            if msg['type'] == 'get_param':
                response = self.server.handle_get_param(msg)
                self.request.sendall(json.dumps(response).encode('utf-8'))
            elif msg['type'] == 'submit_replay':
                response = self.server.handle_submit_replay(msg)
                self.request.sendall(json.dumps(response).encode('utf-8'))
            elif msg['type'] == 'submit_param':
                response = self.server.handle_submit_param(msg)
                self.request.sendall(json.dumps(response).encode('utf-8'))
            elif msg['type'] == 'drain':
                response = self.server.handle_drain()
                self.request.sendall(json.dumps(response).encode('utf-8'))
            else:
                self.request.sendall(json.dumps({'error': 'Unknown message type'}).encode('utf-8'))

    def handle_get_param(self, msg: dict) -> dict:
        with self.state.param_lock:
            if msg.get('model_type') == 'mortal':
                params = self.state.mortal_param
            elif msg.get('model_type') == 'dqn':
                params = self.state.dqn_param
            else:
                return {'error': 'Invalid model type'}
            response = {
                'type': 'get_param_response',
                'params': params,
                'version': self.state.param_version
            }
            return response

    def handle_submit_replay(self, msg: dict) -> dict:
        with self.state.dir_lock:
            replay_data = msg.get('replay_data')
            if not replay_data:
                return {'error': 'No replay data provided'}
            filename = f"replay_{self.state.submission_id}.dat"
            filepath = os.path.join(self.state.buffer_dir, filename)
            try:
                with open(filepath, 'wb') as f:
                    f.write(replay_data)
            except Exception as e:
                return {'error': f'Failed to save replay: {str(e)}'}
            self.state.buffer_size += 1
            self.state.submission_id += 1
            if self.state.buffer_size > self.state.capacity:
                pass
            return {'type': 'submit_replay_response', 'status': 'success', 'filename': filename}

    def handle_submit_param(self, msg: dict) -> dict:
        with self.state.param_lock:
            if self.state.force_sequential:
                if msg.get('version') != self.state.param_version:
                    return {'error': 'Version mismatch'}
            model_type = msg.get('model_type')
            params = msg.get('params')
            if model_type == 'mortal':
                self.state.mortal_param = params
            elif model_type == 'dqn':
                self.state.dqn_param = params
            else:
                return {'error': 'Invalid model type'}
            self.state.param_version += 1
            self.state.idle_param_version = self.state.param_version
            return {'type': 'submit_param_response', 'status': 'success'}

    def handle_drain(self) -> dict:
        with self.state.dir_lock:
            files = [f for f in os.listdir(self.state.buffer_dir) if os.path.isfile(os.path.join(self.state.buffer_dir, f))]
            for file in files:
                src_path = os.path.join(self.state.buffer_dir, file)
                dst_path = os.path.join(self.state.drain_dir, file)
                try:
                    os.rename(src_path, dst_path)
                except Exception as e:
                    return {'error': f'Failed to drain file {file}: {str(e)}'}
            self.state.buffer_size = 0
            return {'type': 'drain_response', 'status': 'success'}

