            self._client_socket.sendall(message.encode())

    def get_client_socket(self):
        self.request_verified.wait()
        return self._client_socket

class MessageTCPHandler(SocketServer.BaseRequestHandler):
    def handle(self):
        self.server._client_socket = self.request
        self.server.request_verified.set()
        try:
            while True:
                data = self.request.recv(1024)
                if not data:
                    break
                self.request.sendall(data)
                self.server.message_handled.set()
        except:
            pass
        finally:
            self.server._client_socket = None
            self.server.request_verified.clear()

