            remote_socket.close()

    def on_recv(self, socket, ipc_pub):
        try:
            byte_msg, sender = socket.recvfrom(2048)
        except OSError:
            return

        if byte_msg[:1] == b"R":
            try:
                ipc_pub.socket.send_string("notify.calibration.add_ref_data", flags=zmq.SNDMORE)
