                response = b"0R"
            except Exception as e:
                response = b"FRReference point mal-formatted or missing: %s" % str(e).encode()
        elif byte_msg[:1] == b"S":
            self.gaze_receiver = sender
            response = b"0S"
        elif byte_msg[:1] == b"s":
