import requests
import json
import hashlib
import hmac

SECRET_KEY = 'your_secret_key'

def request(action, param=None, **params):
    logging.basicConfig(level=logging.DEBUG)
    logging.info("Starting request function")

    merged_params = {**param, **params}

    sorted_params = sorted(merged_params.items())
    param_str = '&'.join(f"{k}={v}" for k, v in sorted_params)
    signature = hmac.new(SECRET_KEY.encode(), param_str.encode(), hashlib.sha256).hexdigest()
    merged_params['signature'] = signature

    url = f"https://api.example.com/api/{action}"
    logging.info(f"Sending request to {url} with parameters {merged_params}")

    proxies = {}
    if 'proxy_host' in merged_params and 'proxy_port' in merged_params:
        proxy_host = merged_params['proxy_host']
        proxy_port = merged_params['proxy_port']
        proxies = {
            'http': f"http://{proxy_host}:{proxy_port}",
            'https': f"https://{proxy_host}:{proxy_port}"
        }
        logging.info(f"Using proxy: {proxy_host}:{proxy_port}")

    headers = {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }

    try:
        response = requests.post(url, json=merged_params, headers=headers, proxies=proxies, timeout=10)
    except requests.exceptions.RequestException as e:
        logging.error(f"Request failed: {e}")
        raise Exception(f"Request failed: {e}")

    if not response.ok:
        logging.error(f"HTTP error: {response.status_code}")
        raise Exception(f"HTTP error: {response.status_code}")

    try:
        data = response.json()
    except json.JSONDecodeError:
        logging.error("Invalid JSON response")
        raise Exception("Invalid JSON response")

    if data.get('error'):
        logging.error(f"API error: {data['error']}")
        raise Exception(f"API error: {data['error']}")

    if data.get('data') is None:
        logging.error("Response data is None")
        raise Exception("Response data is None")

    logging.info("Request successful")
    return data.get('data')

