import requests
import numpy as np
import openai

def get_embedding_from_api(word, model="vicuna-7b-v1.5"):
    if model == "vicuna-7b-v1.5":
        url = "http://localhost:8000/v1/embeddings"
        payload = {"model": model, "input": word}
