import os
import requests

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    if not os.path.exists(wav_path) or not os.path.exists(txt_path):
        print("Error: One or both files do not exist.")
        return None

    if os.path.basename(wav_path) != os.path.basename(txt_path):
        print("Error: WAV and TXT filenames do not match.")
        return None

    with open(wav_path, 'rb') as wav_file, open(txt_path, 'r') as txt_file:
        files = {
            'audio': (os.path.basename(wav_path), wav_file),
            'transcript': (os.path.basename(txt_path), txt_file.read())
        }

        url = f"http://{server_addr}:{port}/process"
        response = requests.post(url, files=files)

    if debug:
        print(response.json())

    return response if response.status_code == 200 else None

