            if response and self._is_size_valid(response):
                return response
            else:
                self.LOGGER.warning(f"Response size invalid for URL: {url}")
                return None
        except Exception as e:
            self.LOGGER.error(f"Error fetching URL {url}: {str(e)}")
            return None

    def _fetch_url(self, url: str, is_threaded: bool, request_args: dict = None) -> str:
        try:
            headers = self.HEADERS.copy()
            if request_args and 'headers' in request_args:
                headers.update(request_args['headers'])
            response = requests.get(url, headers=headers, timeout=10, allow_redirects=True)
            response.raise_for_status()
            content = response.content
            if is_threaded:
                self._results[url] = content
            return content.decode('utf-8', errors='ignore')
        except requests.exceptions.RequestException as e:
            self.LOGGER.error(f"Request error for {url}: {str(e)}")
            return None

    def fetch_urls(self, urls: list, request_args: dict = None) -> dict:
        if not urls or not isinstance(urls, list):
            self.LOGGER.error("Invalid input: URLs must be a non-empty list")
            return {}
        results = {}
        threads = []
        for url in urls:
            if not self._is_valid_url(url):
                self.LOGGER.error(f"Invalid URL: {url}")
                continue
            thread = threading.Thread(target=self._fetch_url_wrapper, args=(url, request_args))
            threads.append(thread)
            thread.start()
        for thread in threads:
            thread.join()
        for url in urls:
            if url in self._results:
                results[url] = self._results[url]
        self._results.clear()
        return results

    def _fetch_url_wrapper(self, url: str, request_args: dict = None):
        result = self._fetch_url(url, is_threaded=True, request_args=request_args)
        if result:
            self._results[url] = result

    def _is_valid_url(self, url: str) -> bool:
        try:
            result = urlparse(url)
            return all([result.scheme, result.netloc])
        except:
            return False

    def _is_size_valid(self, content: bytes) -> bool:
        size = len(content)
        return self.MIN_FILE_SIZE <= size <= self.MAX_FILE_SIZE

