        self.ResponseCls = http.client.HTTPResponse
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries or Retry.DEFAULT_RETRY
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw
        self.headers = headers or {}

    def _new_conn(self) -> http.client.HTTPConnection:
        conn = self.ConnectionCls(self.host, self.port, **self.conn_kw)
        if self.proxy:
            conn.set_proxy(self.proxy, self.proxy_headers)
        return conn

    def _get_conn(self, timeout: Optional[float] = None) -> http.client.HTTPConnection:
        try:
            conn = self.pool.get(block=self.block, timeout=timeout)
            if not conn:
                conn = self._new_conn()
                self.pool.put(conn)
            return conn
        except queue.Empty:
            conn = self._new_conn()
            return conn

    def _put_conn(self, conn: http.client.HTTPConnection) -> None:
        if self.pool.full():
            conn.close()
        else:
            self.pool.put(conn)

    def _make_request(self, conn: http.client.HTTPConnection, method: str, url: str, timeout: float = Timeout.DEFAULT_TIMEOUT, **httplib_request_kw: Any) -> http.client.HTTPResponse:
        parsed_url = urllib.parse.urlparse(url)
        path = parsed_url.path or '/'
        if parsed_url.query:
            path += '?' + parsed_url.query
        conn.request(method, path, body=httplib_request_kw.get('body'), headers=httplib_request_kw.get('headers'))
        return conn.getresponse()

