class HTTPConnection(http.client.HTTPConnection):
    default_port: int = 80
    default_socket_options: List[Tuple[int, int, int]] = [(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)]

    def __init__(self, host: str, port: Optional[int] = None, *, timeout: float = 60, source_address: Optional[Tuple[str, int]] = None, blocksize: int = 16384, socket_options: Optional[List[Tuple[int, int, int]]] = None, proxy: Optional[str] = None, proxy_config: Optional[Any] = None) -> None:
        super().__init__(host, port, timeout=timeout, source_address=source_address)
        self.blocksize = blocksize
        self.socket_options = socket_options or self.default_socket_options
        self.proxy = proxy
        self.proxy_config = proxy_config
        self._has_connected_to_proxy = False
        self._response_options = None
        self._tunnel_host = None
        self._tunnel_port = None
        self._tunnel_scheme = None

    def _new_conn(self) -> socket.socket:
        try:
            sock = super()._new_conn()
            for opt, val in self.socket_options:
                sock.setsockopt(opt, val)
            return sock
        except Exception as e:
            raise ConnectionError(f"Failed to create socket connection: {e}")

    def connect(self) -> None:
        if self.proxy:
            proxy_url = urlparse(self.proxy)
            proxy_host = proxy_url.hostname
            proxy_port = proxy_url.port or self.default_port
            self._tunnel_host = self.host
            self._tunnel_port = self.port
            self._tunnel_scheme = self.scheme
            self.host = proxy_host
            self.port = proxy_port
            self.scheme = 'http'
            self._has_connected_to_proxy = False
        try:
            super().connect()
            if self.proxy and not self._has_connected_to_proxy:
                self._connect_to_proxy()
                self._has_connected_to_proxy = True
        except Exception as e:
            raise ConnectionError(f"Failed to connect to server or proxy: {e}")

    def _connect_to_proxy(self) -> None:
        try:
            proxy_handler = ProxyHandler({'http': self.proxy})
            proxy_handler.http_open = lambda req: self._tunnel_request(req)
            proxy_handler.open = lambda req: self._tunnel_request(req)
            proxy_handler.get_method = lambda req: 'CONNECT'
            proxy_handler.handle = lambda req: self._tunnel_request(req)
        except Exception as e:
            raise URLError(f"Proxy configuration error: {e}")

    def _tunnel_request(self, req) -> Any:
        try:
            req.add_header('Host', f"{self._tunnel_host}:{self._tunnel_port}")
            req.add_header('Connection', 'close')
            response = self._tunnel_http_request(req)
            if response.status == 200:
                self._tunnel_host = None
                self._tunnel_port = None
                self._tunnel_scheme = None
                return response
            else:
                raise URLError(f"Tunnel request failed with status code {response.status}")
        except Exception as e:
            raise URLError(f"Tunnel request error: {e}")

    def _tunnel_http_request(self, req) -> Any:
        try:
            return super().request(req.get_method(), req.get_full_url(), headers=req.headers)
        except Exception as e:
            raise URLError(f"Tunnel HTTP request error: {e}")

    def request(self, method: str, url: str, body: Optional[bytes] = None, headers: Optional[Mapping[str, str]] = None, *, chunked: bool = False, preload_content: bool = True, decode_content: bool = True, enforce_content_length: bool = True) -> None:
        try:
            super().request(method, url, body, headers, chunked=chunked, preload_content=preload_content, decode_content=decode_content, enforce_content_length=enforce_content_length)
        except Exception as e:
            raise RuntimeError(f"Request failed: {e}")

    def getresponse(self) -> Any:
        try:
            return super().getresponse()
        except Exception as e:
            raise RuntimeError(f"Response retrieval failed: {e}")

    def close(self) -> None:
        try:
            super().close()
        except Exception as e:
            raise RuntimeError(f"Connection close failed: {e}")

