def kafka_producer(servers, topic, input_path, timeout):
    try:
        admin_client = AdminClient({'bootstrap.servers': servers})
        metadata = admin_client.list_topics(timeout=5)
        if topic not in set(topic for topic in metadata.topics):
            future = admin_client.create_topics([{
                'topic': topic,
                'replication_factor': 1,
                'partitions': 1
            }])
            future.result()
            logging.info(f"Topic '{topic}' created.")
        else:
            logging.info(f"Topic '{topic}' already exists.")

        conf = {
            'bootstrap.servers': servers,
            'client.id': 'kafka_producer',
            'request.timeout.ms': timeout * 1000,
            'message.timeout.ms': timeout * 1000
        }
        producer = Producer(conf)

        def delivery_report(err, msg):
            if err:
                logging.error(f'Message delivery failed: {err}')
            else:
                logging.info(f'Message delivered to {msg.topic()} [{msg.partition()}]')

        with open(input_path, 'r') as file:
            for line in file:
                line = line.strip()
                if line:
                    producer.produce(topic, key=None, value=line.encode('utf-8'), callback=delivery_report)
                    producer.poll(0)
                    time.sleep(0.1)

        producer.flush(timeout=timeout)
        logging.info("All messages sent successfully.")

    except KafkaException as e:
        logging.error(f"Kafka error: {e.args[0].str()}")
    except Exception as e:
        logging.error(f"Unexpected error: {e}")
    finally:
        if 'producer' in locals():
            producer.close()
        if 'admin_client' in locals():
            admin_client.close()

