def test_kafka_producer_end_to_end(kafka_broker, compression):
    conf = {
        'bootstrap.servers': f'{kafka_broker.host}:{kafka_broker.port}',
        'client.id': 'test-producer',
        'message.timeout.ms': 30000,
        'request.timeout.ms': 30000
    }

    supported_compressions = {
        'none': CompressionType.NONE,
        'gzip': CompressionType.GZIP,
        'snappy': CompressionType.SNAPPY,
        'lz4': CompressionType.LZ4,
        'zstd': CompressionType.ZSTD
    }

    if compression not in supported_compressions:
        pytest.skip(f"Compression type {compression} is not supported")

    try:
        if compression == 'zstd':
            if not hasattr(CompressionType, 'ZSTD'):
                pytest.skip("ZSTD compression is not available in this Kafka version")
        elif compression == 'lz4':
            if not hasattr(CompressionType, 'LZ4'):
                pytest.skip("LZ4 compression is not available in this Kafka version")
    except Exception as e:
        pytest.fail(f"Error checking compression support: {e}")

    try:
        producer = Producer(conf)
        msg_count = 10
        messages = [json.dumps({"key": f"key_{i}", "value": f"value_{i}"}).encode('utf-8') for i in range(msg_count)]

        delivered = 0
        failed = 0

        def delivery_report(err, msg):
            nonlocal delivered, failed
            if err:
                print(f'Message delivery failed: {err}')
                failed += 1
            else:
                print(f'Message delivered to {msg.topic()} [{msg.partition()}]')
                delivered += 1

        for msg in messages:
            producer.produce(
                'test-topic',
                key=msg,
                value=msg,
                callback=delivery_report
            )

        producer.poll(1)
        producer.flush(10)

        assert delivered == msg_count, f"Expected {msg_count} messages, but only {delivered} were delivered"
        assert failed == 0, f"Failed to deliver {failed} messages"

    except Exception as e:
        pytest.fail(f"Test failed: {e}")
    finally:
        producer.close()

