            nonce, msg_type, payload = parse_message(data)
            if not nonce or not msg_type or not payload:
                conn.close()
                break
            if msg_type == b'SESS_KEY_NEG_START':
                session_data['nonce'] = nonce
                session_data['seq'] = 0
                session_data['key'] = generate_nonce()
                session_data['enc_key'] = aes_encrypt(real_key, session_data['key'])
                response = build_message(b'SESS_KEY_NEG_FINISH', session_data['enc_key'])
                conn.sendall(response)
            elif msg_type == b'DP_QUERY_NEW':
                seq = session_data['seq']
