        except Exception as e:
            print(f"Error handling client {addr}: {e}")
            conn.close()
            break

def parse_message(data):
    nonce_len = struct.unpack('<I', data[:4])[0]
    nonce = data[4:4+nonce_len]
    msg_type = data[4+nonce_len:4+nonce_len+4]
    payload = data[4+nonce_len+4:]
    return nonce, msg_type, payload

def build_message(msg_type, payload):
    nonce = generate_nonce()
    nonce_len = len(nonce)
    header = struct.pack('<I', nonce_len) + nonce + msg_type
