import os

class UrlFile:
    def __init__(self, url: str, name: Optional[str] = None):
        self.url = url
        self.name = name
        self._temp_file_path = None

    def get_filename(self) -> Optional[str]:
        if self.name:
            return self.name
        try:
            response = httpx.get(self.url, timeout=10)
            response.raise_for_status()
            content_disposition = response.headers.get('content-disposition')
            if content_disposition:
                import re
                filename_match = re.search(r'filename[*]?=([^;]+)', content_disposition)
                if filename_match:
                    filename = filename_match.group(1).strip().strip('"').strip("'")
                    return filename
        except Exception as e:
            pass
        return os.path.basename(self.url)

    def read(self):
        if not self.name:
            self.name = self.get_filename()
        if not self.name:
            raise ValueError("Filename could not be determined")

        with tempfile.TemporaryDirectory() as tmpdir:
            temp_file_path = os.path.join(tmpdir, self.name)
            try:
                with httpx.stream("GET", self.url, timeout=10) as response:
                    response.raise_for_status()
                    with open(temp_file_path, "wb") as f:
                        for chunk in response.iter_content(chunk_size=8192):
                            f.write(chunk)
            except Exception as e:
                raise RuntimeError(f"Failed to download file: {e}")

            if not os.path.exists(temp_file_path):
                raise RuntimeError("Downloaded file not found in temporary directory")

            self._temp_file_path = temp_file_path
            return open(self._temp_file_path, "rb")

