import socket
import struct
import ctypes

class UDPSocketHandler:
    IP_BINDANY = 1
    IP_RECVDSTADDR = 21

    @staticmethod
    def recv_udp(listener, bufsize):
        data, addr = listener.recvfrom(bufsize)
        srcip = addr
        dstip = None
        try:
            if hasattr(listener, 'getsockopt'):
                level = socket.SOL_IP
                optname = UDPSocketHandler.IP_RECVDSTADDR
                buffer = ctypes.create_string_buffer(b'\0' * 16)
                len_buffer = ctypes.c_int(16)
                listener.getsockopt(level, optname, buffer, len_buffer)
                dstip = struct.unpack('!4sH', buffer.raw[:struct.calcsize('!4sH')])
                dstip = (dstip[0].decode(), dstip[1])
        except Exception as e:
            pass
        return srcip, dstip, data

    @staticmethod
    def send_udp(sock, srcip, dstip, data):
        src_addr = socket.sockaddr_in(*srcip)
        sock.bind(src_addr)
        sock.sendto(data, dstip)

    @staticmethod
    def setup_udp_listener(udp_listener):
        for sock in udp_listener:
            if hasattr(sock, 'setsockopt'):
                sock.setsockopt(socket.SOL_IP, UDPSocketHandler.IP_RECVDSTADDR, 1)

