import socket
import errno
import logging
from typing import Tuple, Optional

class UDPRelay:
    def __init__(self, config, dns_resolver, is_local):
        self._config = config
        self._dns_resolver = dns_resolver
        self._is_local = is_local
        self._sockets = set()
        self._server_socket = None
        self._closed = False

        if is_local:
            self._listen_addr = config['local_address']
            self._listen_port = config['local_port']
        else:
            self._listen_addr = config['server']
            self._listen_port = config['server_port']

        addrs = socket.getaddrinfo(self._listen_addr, self._listen_port, 0,
                                 socket.SOCK_DGRAM, socket.SOL_UDP)
        af, socktype, proto, canonname, sa = addrs[0]
        self._server_socket = socket.socket(af, socktype, proto)
        self._server_socket.bind((self._listen_addr, self._listen_port))
        self._server_socket.setblocking(False)

    def _handle_server(self):
        data, r_addr = self._server_socket.recvfrom(65536)
        if not data:
            logging.debug('UDP handle_server: data is empty')
            return

        try:
            header_result = parse_header(data)
            if header_result is None:
                return
            connecttype, addrtype, dest_addr, dest_port, header_length = header_result

            if self._is_local:
                server_addr, server_port = self._get_a_server()
            else:
                server_addr, server_port = dest_addr, dest_port

            if (addrtype & 7) == 3:
                af = common.is_ip(server_addr)
                if not af:
                    handler = common.UDPAsyncDNSHandler((data, r_addr, None, header_length))
                    handler.resolve(self._dns_resolver, (server_addr, server_port), 
                                   self._handle_server_dns_resolved)
                else:
                    self._handle_server_dns_resolved("", (server_addr, server_port), 
                                                   server_addr, (data, r_addr, None, header_length))
            else:
                self._handle_server_dns_resolved("", (server_addr, server_port), 
                                               server_addr, (data, r_addr, None, header_length))
        except Exception as e:
            logging.error(f"Error handling server data: {e}")

    def _handle_server_dns_resolved(self, error, remote_addr, server_addr, params):
        if error:
            return
        data, r_addr, uid, header_length = params
        server_port = remote_addr[1]

        addrs = socket.getaddrinfo(server_addr, server_port, 0,
                                 socket.SOCK_DGRAM, socket.SOL_UDP)
        af, socktype, proto, canonname, sa = addrs[0]
        client = socket.socket(af, socktype, proto)
        client.setblocking(False)

        try:
            client.sendto(data[header_length:], (server_addr, server_port))
            self._sockets.add(client.fileno())
        except IOError as e:
            err = eventloop.errno_from_exception(e)
            if err not in (errno.EINPROGRESS, errno.EAGAIN):
                logging.error(f"Error sending UDP data: {e}")

    def _handle_client(self, sock):
        data, r_addr = sock.recvfrom(65536)
        if not data:
            return

        if not self._is_local:
            data = pack_addr(r_addr[0]) + struct.pack('>H', r_addr[1]) + data

        try:
            self._server_socket.sendto(data, r_addr)
        except (OSError, IOError) as e:
            error_no = eventloop.errno_from_exception(e)
            if error_no not in (errno.EWOULDBLOCK,):
                logging.error(f"Error sending UDP response: {e}")

    def close(self):
        self._closed = True
        if self._server_socket:
            self._server_socket.close()
        for sock in self._sockets:
            try:
                sock.close()
            except:
                pass

