        def accept(offers):
            for offer in offers:
                if isinstance(offer, PerMessageDeflateOffer):
                    return PerMessageDeflateOfferAccept(offer)

        self.factory.setProtocolOptions(perMessageCompressionAccept=accept)

    def run(self):
        reactor.listenTCP(self.port, self.factory)
        reactor.run()

