import requests
import json
import logging
from urllib.parse import urlencode
from datetime import datetime

logging.basicConfig(level=logging.INFO)

API_BASE_URL = "https://api.example.com"
SIGNING_SECRET = "your_signing_secret"

def request(action, param=None, **params):
    if param is None:
        param = {}
    all_params = {**param, **params}
    signed_params = sign_params(all_params)

    url = f"{API_BASE_URL}/api/{action}"
    headers = {
        "Content-Type": "application/json",
        "Authorization": f"Bearer {generate_token()}"
    }

    try:
        response = send_request(url, signed_params, headers)
        response.raise_for_status()

        data = response.json()

        if data.get('error'):
            raise Exception(f"API error: {data['error']}")

        if not data.get('data'):
            raise Exception("Response data is None")

        return data['data']

    except requests.exceptions.RequestException as e:
        logging.error(f"Request failed: {e}")
        raise Exception("Request failed") from e
    except json.JSONDecodeError as e:
        logging.error(f"Failed to decode JSON response: {e}")
        raise Exception("Invalid JSON response") from e
    except Exception as e:
        logging.error(f"Unexpected error: {e}")
        raise

def sign_params(params):
    timestamp = datetime.now().isoformat()
    params['timestamp'] = timestamp
    params['signature'] = generate_signature(params)
    return params

def generate_signature(params):
    sorted_params = sorted(params.items())
    query_string = urlencode(sorted_params)
    return hmac.new(SIGNING_SECRET.encode(), query_string.encode(), hashlib.sha256).hexdigest()

def generate_token():
    return "your_access_token"

def send_request(url, params, headers):
    proxies = get_proxies()
    return requests.post(url, json=params, headers=headers, proxies=proxies)

def get_proxies():
    proxy_url = os.getenv("HTTP_PROXY") or os.getenv("HTTPS_PROXY")
    if proxy_url:
        return {"http": proxy_url, "https": proxy_url}
    return {}

