import os
import requests
import json
from pyflink.table import ScalarFunction, DataTypes

class GetLocation(ScalarFunction):
    def eval(self, ip_address: str) -> str:
        api_key = os.environ.get('API_KEY')
        if not api_key:
            return json.dumps({})
        url = f"https://api.ip2location.io/?key={api_key}&ip={ip_address}"
        try:
            response = requests.get(url, timeout=10)
            if response.status_code == 200:
                data = response.json()
                country = data.get('country_name', '')
                state = data.get('region_name', '')
                city = data.get('city_name', '')
                return json.dumps({'country': country, 'state': state, 'city': city})
            else:
                return json.dumps({})
        except Exception as e:
            return json.dumps({})

