import requests
import logging
import concurrent.futures

class SimpleCrawler:
    def __init__(self):
        self._results = {}
        self.MAX_FILE_SIZE = 20 * 1024 * 1024
        self.MIN_FILE_SIZE = 10
        self.LOGGER = logging.getLogger(__name__)
        self.USER_AGENT = 'SimpleCrawler/1.0'
        self.HEADERS = {'User-Agent': self.USER_AGENT}

    def fetch_url(self, url, request_args=None):
        return self._fetch_url(url, False, request_args)

    def _fetch_url(self, url, is_threaded, request_args=None):
        try:
            headers = self.HEADERS.copy()
            if request_args and 'headers' in request_args:
                headers.update(request_args['headers'])
            timeout = request_args.get('timeout') if request_args else 10
            response = requests.get(url, headers=headers, timeout=timeout)
            if response.status_code != 200:
                self.LOGGER.error(f"Non-200 status code for {url}: {response.status_code}")
                if is_threaded:
                    self._results[url] = None
                return
            content = response.content
            content_size = len(content)
            if content_size < self.MIN_FILE_SIZE or content_size > self.MAX_FILE_SIZE:
                self.LOGGER.warning(f"Content size {content_size} for {url} is out of allowed range")
                if is_threaded:
                    self._results[url] = None
                return
            if is_threaded:
                self._results[url] = content.decode('utf-8')
            else:
                return content.decode('utf-8')
        except requests.exceptions.RequestException as e:
            self.LOGGER.error(f"Request error for {url}: {e}")
            if is_threaded:
                self._results[url] = None
        except Exception as e:
            self.LOGGER.error(f"Unexpected error for {url}: {e}")
            if is_threaded:
                self._results[url] = None

    def fetch_urls(self, urls, request_args=None):
        self._results = {}
        if not urls:
            return self._results
        with concurrent.futures.ThreadPoolExecutor() as executor:
            futures = [executor.submit(self._fetch_url, url, True, request_args) for url in urls]
            concurrent.futures.wait(futures)
        return self._results.copy()

