import requests

class PlexMediaServer:
    TIMEOUT = 10

    def __init__(self, mediaserver_instance):
        self.mediaserver_instance = mediaserver_instance

    def make_request(self, uri='/', params={}):
        url = self.mediaserver_instance.base_url + uri
        headers = {'X-Plex-Token': self.mediaserver_instance.token}
        verify = self.mediaserver_instance.verify_ssl
        response = requests.get(url, headers=headers, params=params, verify=verify, timeout=self.TIMEOUT)
        return response

    def validate(self):
        response = self.make_request('/')
        if response.status_code != 200:
            raise Exception("Failed to connect to Plex server")
        sections_response = self.make_request('/library/sections')
        sections = sections_response.json()
        for lib in self.mediaserver_instance.libraries:
            found = False
            for section in sections:
                if section['key'] == str(lib) or section['title'] == lib:
                    found = True
                    break
            if not found:
                raise Exception(f"Library {lib} not found on server")
        return True

    def update(self):
        for lib_id in self.mediaserver_instance.libraries:
