        self.PULSSTORY_CLIENT_ID = client_id
        self.PULSSTORY_CLIENT_SECRET = client_secret

    def _api_request(self, endpoint, service_record=None, method="POST", data=None, headers=None, files=None):
        url = f"{self.URLBase}{endpoint}"
        headers = headers or {}
        if service_record and hasattr(service_record, 'access_token'):
            headers['Authorization'] = f'Bearer {service_record.access_token}'
        if method == "POST":
            if data:
                headers['Content-Type'] = 'application/json'
                response = requests.post(url, json=data, headers=headers)
            elif files:
                response = requests.post(url, files=files, headers=headers)
            else:
                response = requests.post(url, headers=headers)
        elif method == "GET":
            response = requests.get(url, headers=headers)
        else:
            raise ValueError(f"Unsupported HTTP method: {method}")
        try:
            response.raise_for_status()
        except requests.exceptions.HTTPError as e:
            error_response = response.json()
            raise Exception(f"API Error {response.status_code}: {error_response.get('error', 'Unknown error')}")
        return response.json()

    def _apiData(self, service_record):
        return {
            'grant_type': 'authorization_code',
            'code': service_record.code,
            'client_id': self.PULSSTORY_CLIENT_ID,
            'client_secret': self.PULSSTORY_CLIENT_SECRET
        }

    def get_user_id(self, service_record):
        uris = self.get_api_uris(service_record)
        response = self._api_request(uris['user'], service_record, method="GET")
