        return response.get('access_token')

    def revoke_authorization(self, service_record):
        uris = self.get_api_uris(service_record)
        headers = {'Authorization': f'Bearer {service_record.access_token}'}
        response = self._api_request(uris['revoke'], service_record, method="POST", headers=headers)
        return response.get('success')

    def download_activity(self, service_record, activity_id):
        uris = self.get_api_uris(service_record)
        params = {'activity_id': activity_id}
        response = self._api_request(uris['activity'], service_record, method="GET", params=params)
        return response

