                    'name': name,
                    'type': 'http',
                    'server': server,
                    'port': port,
                    'username': self.fake.user_name(),
                    'password': self.fake.password()
                })
            config = 'proxies:\n' + '\n'.join([f'  - {name}: {server}:{port} (user: {username}, pass: {password})' for name, server, port, username, password in proxies])
            return config
        elif sub_type == 'wireguard':
            config = '[Interface]\n'
            config += f'PrivateKey = {self.fake.sha256()}\n'
            config += 'Address = 10.0.0.1/24\n'
            config += 'ListenPort = 51820\n\n'
            config += '[Peer]\n'
            config += f'PublicKey = {self.fake.sha256()}\n'
            config += 'Endpoint = example.com:51820\n'
            config += 'AllowedIPs = 0.0.0.0/0\n'
            return config
        elif sub_type == 'surge':
            config = 'proxies:\n'
            config += '  - name: Test Proxy\n'
            config += '    type: http\n'
            config += '    server: example.com\n'
            config += '    port: 8080\n'
            config += '    username: user\n'
            config += '    password: pass\n'
            return config
        else:
            return 'Unsupported subscription type', 400

    def run(self, host: str = '0.0.0.0', port: int = 5000, debug: bool = True):
        self.app.run(host=host, port=port, debug=debug)

