from langchain.docstore.document import Document
from langchain.embeddings import GPT4AllEmbeddings
from langchain.text_splitter import RecursiveCharacterTextSplitter
from langchain.vectorstores import FAISS
from langchain.chains import RetrievalQA
from langchain.llms import GPT4All
from gpt4all import GPT4All

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        CORS(self.app)
        self.embeddings_model_name = "mistral-7b"
        self.persist_directory = "vectorstore"
        self.model_type = "gpt4all"
        self.model_path = "models/gpt4all.bin"
        self.model_n_ctx = "2048"
        self.llm = GPT4All(model=self.model_path, model_type=self.model_type, n_ctx=int(self.model_n_ctx))
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route('/ingest', methods=['GET'])
        def ingest_data():
            source_dir = request.args.get('source_dir', default='documents', type=str)
            documents = self._load_documents(source_dir)
            return jsonify({"status": "success", "message": f"Ingested {len(documents)} documents"})

        @self.app.route('/get_answer', methods=['POST'])
        def get_answer():
            data = request.get_json()
            question = data.get('question', '')
            if not question:
                return jsonify({"error": "No question provided"}), 400
            answer = self._answer_question(question)
            return jsonify({"answer": answer})

        @self.app.route('/download_model', methods=['GET'])
        def download_and_save():
            return send_file(self.model_path, as_attachment=True)

    def _load_documents(self, source_dir):
        documents = []
        for root, _, files in os.walk(source_dir):
            for file in files:
                file_path = os.path.join(root, file)
                doc = self._load_single_document(file_path)
                documents.append(doc)
        return documents

    def _load_single_document(self, file_path):
        with open(file_path, 'r', encoding='utf-8') as f:
            text = f.read()
        return Document(page_content=text, metadata={"source": file_path})

    def run(self):
        self.app.run(host='0.0.0.0', port=5000)

    def _load_model(self):
        if not os.path.exists(self.model_path):
            raise FileNotFoundError(f"Model file not found at {self.model_path}")
        self.llm = GPT4All(model=self.model_path, model_type=self.model_type, n_ctx=int(self.model_n_ctx))

    def _answer_question(self, question):
        if not hasattr(self, 'vectorstore'):
            self._load_model()
            texts = [doc.page_content for doc in self._load_documents("documents")]
            text_splitter = RecursiveCharacterTextSplitter(chunk_size=1000, chunk_overlap=200)
            texts = text_splitter.split_text("\n".join(texts))
            self.vectorstore = FAISS.from_texts(texts, GPT4AllEmbeddings())
        retriever = self.vectorstore.as_retriever()
        qa_chain = RetrievalQA.from_chain_type(self.llm, retriever=retriever)
        return qa_chain.run(question)

