    def __init__(self, deployment_name, rest_host, rest_port, rest_procs):
        threading.Thread.__init__(self)
        self._stop_event = threading.Event()
        self.server = self._create_server(deployment_name, rest_host, rest_port, rest_procs)

    def _create_server(self, deployment_name, rest_host, rest_port, rest_procs):
        app = Flask("RestfulGateway")

        @app.route("/terminate", methods=["GET"])
        def terminate():
            threading.Thread(target=self._shutdown).start()
            return "Shutting down RESTful API gateway server"

        @app.route("/healthz", methods=["GET"])
        def healthz():
            return "ok"

        api = Api(app)
        path = "/{}/{}".format(RESTFUL_API_PATH, deployment_name)
        api.add_resource(RestfulGatewayService, path, resource_class_args=(deployment_name,))

        return make_server(rest_host, rest_port, app, threaded=False, processes=rest_procs)

    def _shutdown(self):
        time.sleep(RESTFUL_GATEWAY_SHUTDOWN_TIMEOUT)
        self.server.shutdown()

    def run(self):
        self.server.serve_forever()
        self._stop_event.set()

    def get_stop_event(self):
        return self._stop_event

