from google.cloud import bigquery
from google.api_core.exceptions import NotFound

app = Flask(__name__)

client = bigquery.Client()
dataset_id = 'argo_events'
table_id = 'events'

def generate_signature(event_data, message_id):
    payload = json.dumps({
        'event_data': event_data,
        'message_id': message_id
    }, sort_keys=True)
    return hashlib.sha256(payload.encode('utf-8')).hexdigest()

def process_argocd_event(msg):
    try:
        data = base64.b64decode(msg['message']['data']).decode('utf-8')
        event_data = json.loads(data)
        message_id = msg['message']['message_id']
        attributes = msg['message'].get('attributes', {})
        event = {
            'event_id': message_id,
            'timestamp': datetime.utcnow().isoformat(),
            'event_type': event_data.get('type', 'unknown'),
            'event_data': event_data,
            'attributes': attributes,
            'signature': generate_signature(event_data, message_id)
        }
        return event
    except (KeyError, json.JSONDecodeError, base64.binascii.Error) as e:
        raise ValueError(f"Invalid message data: {e}")

@app.route('/process', methods=['POST'])
def index():
    try:
        data = request.json
        if not data or 'message' not in data:
            return jsonify({'error': 'Invalid request format'}), 400
        msg = data['message']
        if 'data' not in msg or 'message_id' not in msg:
            return jsonify({'error': 'Missing required fields in message'}), 400
        processed_event = process_argocd_event(msg)
        dataset_ref = client.dataset(dataset_id)
        table_ref = dataset_ref.table(table_id)
        try:
            table = client.get_table(table_ref)
        except NotFound:
            logging.error(f"Table {table_id} not found in dataset {dataset_id}")
            return jsonify({'error': 'BigQuery table not found'}), 500
        rows_to_insert = [processed_event]
        errors = client.insert_rows_json(table, rows_to_insert)
        if errors:
            logging.error(f"Errors inserting rows: {errors}")
            return jsonify({'error': 'Failed to insert into BigQuery'}), 500
        return '', 204
    except ValueError as e:
        logging.error(f"Value error: {e}")
        return jsonify({'error': 'Invalid payload'}), 400
    except Exception as e:
        logging.error(f"Unexpected error: {e}")
        return jsonify({'error': 'Internal server error'}), 500

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=5000)

