def trace(files=None, port=9999, host='127.0.0.1', browser=False, daemon=False):
    line_counts = {}
    stack_traces = []

    def tracer(frame, event, arg):
        if event != 'line':
            return tracer
        filename = frame.f_code.co_filename
        if files is not None and not files(filename):
            return tracer
        line = frame.f_lineno
        if line not in line_counts:
            line_counts[line] = 0
        line_counts[line] += 1
        stack = []
        current_frame = frame
        while current_frame:
            stack.append(current_frame)
            current_frame = current_frame.f_back
        stack_traces.append(stack)
        return tracer

    app = Flask(__name__)

    @app.route('/')
    def index():
        return """
        <html>
        <body>
            <h1>Code Tracing Visualization</h1>
