from io import BytesIO
import secrets

app = Flask(__name__)

sessions = {}

def generate_auth_code(length=16):
    return secrets.token_hex(length)

def generate_token(length=16):
    return secrets.token_hex(length)

@app.route('/get_qrcode', methods=['GET'])
def get_qrcode():
    sid = str(uuid.uuid4())
    auth_code = generate_auth_code()
    sessions[sid] = {'auth_code': auth_code, 'status': 'pending'}
    qr = qrcode.make(auth_code)
    buffered = BytesIO()
    qr.save(buffered, format="PNG")
    img_str = base64.b64encode(buffered.getvalue()).decode()
    data_uri = f"data:image/png;base64,{img_str}"
    return jsonify({'qrcode_url': data_uri, 'sid': sid})

@app.route('/check_qrcode/<sid>', methods=['GET'])
def check_qrcode(sid):
    session = sessions.get(sid)
    if not session:
        return jsonify({'error': 'Session not found'}), 404
    return jsonify({'status': session['status']})

@app.route('/get_tokens', methods=['POST'])
def get_tokens():
    data = request.get_json()
    auth_code = data.get('auth_code')
    if not auth_code:
        return jsonify({'error': 'Missing auth_code'}), 400
    for sid, session in sessions.items():
        if session['auth_code'] == auth_code and session['status'] == 'pending':
            token = generate_token()
            session['status'] = 'completed'
            return jsonify({'status': 'success', 'token': token})
    return jsonify({'error': 'Invalid auth_code'}), 400

if __name__ == '__main__':
    app.run(debug=True)

