from flask import Flask, request, redirect, abort, jsonify
import random
import string

class BlueprintServer:
    def __init__(self):
        self.app = Flask(__name__)
        self.browser_pattern = re.compile(r'Mozilla|Firefox|Chrome|Safari|Edge')
        self._setup_routes()
        self.request_count = 0
        self.bandwidth_used = 0

        self.app.before_request(self._before_request)
        self.app.after_request(self._after_request)

    def _setup_routes(self):
        self.app.add_url_rule('/secret', 'secret', self.secret, methods=['POST'])
        self.app.add_url_rule('/blueprints/<name>', 'blueprint', self.blueprint, methods=['GET', 'PUT'])
        self.app.add_url_rule('/blueprints/<name>/tarballs/<sha>', 'tarball', self.tarball, methods=['GET', 'PUT'])
        self.app.add_url_rule('/blueprints/<name>/sh/<name2>', 'sh', self.sh, methods=['GET'])
        self.app.add_url_rule('/blueprints/<name>/user-data', 'user_data', self.user_data, methods=['GET'])

    def _before_request(self):
        self.request_count += 1

    def _after_request(self, response):
        self.bandwidth_used += len(response.data)
        return response

    def _validate_secret(self, secret):
        if not re.match(r'^[a-zA-Z0-9]{32}$', secret):
            abort(400)

    def _validate_name(self, name):
        if not re.match(r'^[a-zA-Z0-9_-]+$', name):
            abort(400)

    def _validate_sha(self, sha):
        if not re.match(r'^[a-fA-F0-9]{64}$', sha):
            abort(400)

    def _blueprint(self, secret, name):
        if not self._validate_secret(secret):
            return None
        if name not in self._get_blueprints():
            return None
        return True

    def _get_blueprints(self):
        return ['example-blueprint']

    def _server(self):
        return 'http://localhost:5000'

    def secret(self):
        secret = ''.join(random.choices(string.ascii_letters + string.digits, k=32))
        return MeteredResponse(secret, 201)

    def blueprint(self, name):
        secret = request.args.get('secret')
        if not secret:
            abort(400)
        self._validate_secret(secret)
        self._validate_name(name)
        if request.method == 'GET':
            return self.get_blueprint(secret, name)
        else:
            return self.put_blueprint(secret, name)

    def get_blueprint(self, secret, name):
        if not self._blueprint(secret, name):
            abort(404)
        blueprint_data = {'name': name, 'secret': secret}
        return MeteredResponse(blueprint_data, 200)

    def put_blueprint(self, secret, name):
        self._validate_secret(secret)
        self._validate_name(name)
        if name in self._get_blueprints():
            abort(400)
        self._get_blueprints().append(name)
        return MeteredResponse({'status': 'stored'}, 202)

