import json
import flask
from flask import Flask, jsonify, request

app = Flask(__name__)

def load_colors():
    try:
        with open('colors.json', 'r') as f:
            return json.load(f)
    except (FileNotFoundError, json.JSONDecodeError):
        return []

def save_colors(colors):
    with open('colors.json', 'w') as f:
        json.dump(colors, f, indent=4)

@app.route('/colors', methods=['GET'])
def get_colors():
    colors = load_colors()
    return jsonify(colors)

@app.route('/colors/<name>', methods=['GET'])
def get_color(name):
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            return jsonify(color)
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    if not data:
        return jsonify({'error': 'Invalid JSON'}), 400
    if 'name' not in data or 'value' not in data:
        return jsonify({'error': 'Missing name or value'}), 400
    colors = load_colors()
    if any(c['name'] == data['name'] for c in colors):
        return jsonify({'error': 'Color already exists'}), 400
    colors.append(data)
    save_colors(colors)
    return jsonify(data), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            if 'value' in data:
                color['value'] = data['value']
            save_colors(colors)
            return jsonify(color)
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            colors.remove(color)
            save_colors(colors)
            return jsonify(color)
    return jsonify({'error': 'Color not found'}), 404

if __name__ == '__main__':
    app.run(debug=True)

