        self.authorization = authorization
        self.allow_profiler = allow_profiler
        self.profiler_output = profiler_output
        self.profile_by_default = profile_by_default
        self.allow_add = allow_add
        self.logfile = logfile
        self.loglevel = loglevel
        self.log_exception_formatter = log_exception_formatter

        logging.basicConfig(
            level=logging.getLevelName(self.loglevel),
            format='%(asctime)s - %(levelname)s - %(message)s',
            stream=self.logfile
        )

        self.logger = logging.getLogger(__name__)

        if self.log_exception_formatter:
            self.logger.handlers[0].formatter = self.log_exception_formatter

        CORS(self.app, resources={r"/*": {"origins": "*"}})

        self.api = Blueprint('api', __name__)
        self.app.register_blueprint(self.api)

        @self.api.route('/compserver', methods=['POST', 'HEAD', 'OPTIONS'])
        def compserver():
            return self._compserver()

        self.app.register_error_handler(Exception, self._handle_exception)

    def _compserver(self):
        if request.method == 'OPTIONS':
            return self._options_response()

        if request.method == 'HEAD':
            return self._head_response()

        if not self._validate_content_type():
            return jsonify({'error': 'Unsupported content type'}), 415

        try:
            data = self._parse_request_data()
            result = self._compute(data)
            return self._serialize_response(result)
        except Exception as e:
            self.logger.error(f"Error processing request: {str(e)}", exc_info=True)
            return jsonify({'error': str(e)}), 500

    def _options_response(self):
        return jsonify({
            'supported_formats': self.formats,
            'allowed_methods': ['POST', 'HEAD', 'OPTIONS']
        })

    def _head_response(self):
        return '', 200

    def _validate_content_type(self):
        content_type = request.headers.get('Content-Type', '').split(';')[0]
        return content_type in self.formats

    def _parse_request_data(self):
        content_type = request.headers.get('Content-Type', '').split(';')[0]
        if content_type == 'application/json':
            return request.get_json()
        elif content_type == 'application/x-www-form-urlencoded':
            return request.form.to_dict()
        elif content_type == 'text/plain':
            return request.data.decode('utf-8')
        else:
            raise ValueError(f"Unsupported content type: {content_type}")

    def _compute(self, data):
        # Placeholder for actual computation logic
        return {'result': data}

    def _serialize_response(self, data):
        if 'json' in self.formats:
            return jsonify(data)
        else:
            raise ValueError("Unsupported serialization format")

    def _handle_exception(self, e):
        self.logger.error(f"Unhandled exception: {str(e)}", exc_info=True)
        return jsonify({'error': str(e)}), 500

    def run(self, port=6363, retry=False, **kwargs):
        for i in range(1 if not retry else 10):
            try:
                self.app.run(port=port, **kwargs)
                break
            except OSError as e:
                if retry and e.errno == 13:
                    self.logger.warning(f"Port {port} is busy, retrying...")
                    time.sleep(1)
                else:
                    raise
        else:
            self.logger.error(f"Failed to start server on port {port} after multiple retries")

