@app.route('/trade')
def trade():
    data_str = request.args.get('data')
    if not data_str:
        return jsonify({'error': 'Missing data parameter'}), 400
    try:
        trade_data = json.loads(data_str)
    except json.JSONDecodeError:
        return jsonify({'error': 'Invalid JSON data'}), 400
    asset = trade_data.get('asset')
    quantity = trade_data.get('quantity')
    trade_type = trade_data.get('type')
    if not all([asset, quantity, trade_type]):
        return jsonify({'error': 'Missing asset, quantity, or type'}), 400
    try:
        quantity = int(quantity)
    except ValueError:
        return jsonify({'error': 'Invalid quantity'}), 400
    if trade_type == 'buy':
        if balance >= quantity:
            balance -= quantity
            inventory[asset] = inventory.get(asset, 0) + quantity
            queue.append({'action': 'buy', 'asset': asset, 'quantity': quantity})
            return jsonify({'result': 'Trade executed', 'balance': balance, 'inventory': inventory})
        else:
            return jsonify({'error': 'Insufficient balance'})
    elif trade_type == 'sell':
        if inventory.get(asset, 0) >= quantity:
            balance += quantity
            inventory[asset] = inventory.get(asset, 0) - quantity
            queue.append({'action': 'sell', 'asset': asset, 'quantity': quantity})
            return jsonify({'result': 'Trade executed', 'balance': balance, 'inventory': inventory})
        else:
            return jsonify({'error': 'Insufficient inventory'})
    else:
        return jsonify({'error': 'Invalid trade type'})

