        self.app.route('/api/scans/<sid>/list')(self.api_list)
        self.app.route('/api/scans/<sid>/stop', methods=['POST'])(self.api_stop)

    def _start_janitor(self):
        cleaner = threading.Thread(target=self.janitor, args=(self.sessions,))
        cleaner.daemon = True
        cleaner.start()

    def janitor(self, sessions):
        while True:
            time.sleep(1)
            for s in sorted(sessions, key=lambda x: x.timestamp):
                if s.jobs.empty() and s.threads:
                    s.stop()
