class DNSTwistAPI:
    def __init__(self, host='0.0.0.0', port=5000):
        from flask import Flask, request, jsonify, Response
        import threading
        import time
        import dnstwist
        self.app = Flask(__name__)
        self.sessions = []
        self.timeout = 300  # 5 minutes
        self._setup_routes()
        self.janitor_thread = threading.Thread(target=self.janitor, daemon=True)
        self.janitor_thread.start()
        self.run(host, port)

    def _setup_routes(self):
        from flask import request, jsonify, Response
        @self.app.route('/')
        def root():
            return """
            <h1>DNSTwist API</h1>
            <p>Use /api/scan to start a domain scan</p>
            """

        @self.app.route('/api/scan', methods=['POST'])
        def api_scan():
            domain = request.json.get('domain')
            if not domain:
                return jsonify({'error': 'Missing domain parameter'}), 400
            sid = self._generate_sid()
            session = {
                'sid': sid,
                'domain': domain,
                'status': 'running',
                'start_time': time.time(),
                'results': [],
                'permutations': []
            }
            self.sessions.append(session)
            try:
                permutations = dnstwist.dnstwist(domain)
                session['permutations'] = permutations
                return jsonify({'sid': sid, 'status': 'started'}), 201
            except Exception as e:
                return jsonify({'error': str(e)}), 500

        @self.app.route('/api/status/<sid>', methods=['GET'])
        def api_status(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] == 'completed':
                return jsonify({'status': 'completed', 'results': len(session['results'])}), 200
            elif session['status'] == 'running':
                return jsonify({'status': 'running'}), 200
            else:
                return jsonify({'status': 'expired'}), 410

        @self.app.route('/api/domains/<sid>', methods=['GET'])
        def api_domains(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed'}), 400
            return jsonify({'domains': session['results']}), 200

        @self.app.route('/api/csv/<sid>', methods=['GET'])
        def api_csv(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed'}), 400
            csv_data = 'Domain,Registered\n'
            for domain in session['results']:
                csv_data += f"{domain['domain']},{domain['registered']}\n"
            return Response(csv_data, mimetype='text/csv'), 200

        @self.app.route('/api/json/<sid>', methods=['GET'])
        def api_json(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed'}), 400
            return jsonify({'results': session['results']}), 200

        @self.app.route('/api/list/<sid>', methods=['GET'])
        def api_list(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] != 'completed':
                return jsonify({'error': 'Scan not completed'}), 400
            return jsonify({'results': [d['domain'] for d in session['results']]}), 200

        @self.app.route('/api/stop/<sid>', methods=['POST'])
        def api_stop(sid):
            session = next((s for s in self.sessions if s['sid'] == sid), None)
            if not session:
                return jsonify({'error': 'Session not found'}), 404
            if session['status'] == 'running':
                session['status'] = 'stopped'
                return jsonify({'status': 'stopped'}), 200
            else:
                return jsonify({'error': 'Session not running'}), 400

    def _generate_sid(self):
        import uuid
        return str(uuid.uuid4())

    def janitor(self):
        while True:
            time.sleep(self.timeout / 2)
            now = time.time()
            self.sessions = [s for s in self.sessions if s['status'] != 'expired']
            for session in self.sessions:
                if session['status'] == 'running' and (now - session['start_time'] > self.timeout):
                    session['status'] = 'expired'

    def run(self, host, port):
        self.app.run(host=host, port=port)

