    if request.content_type == 'application/json':
        data = request.get_json()
        text = data.get('text')
        if not text:
            return jsonify({'error': 'Missing text field'}), 400
        predictions = predict(text)
        return jsonify(predictions)
    elif request.content_type == 'text/csv':
        csv_data = request.data.decode('utf-8')
        csv_file = io.StringIO(csv_data)
        reader = csv.DictReader(csv_file)
        rows = [row for row in reader]
        results = []
        for row in rows:
            text = row.get('text')
            if not text:
                continue
            predictions = predict(text)
            results.append({'text': text, 'predictions': predictions})
        output = io.StringIO()
        writer = csv.DictWriter(output, fieldnames=['text', 'predictions'])
        writer.writeheader()
        writer.writerows(results)
        return output.getvalue(), 200, {'Content-Type': 'text/csv'}
    elif request.content_type == 'text/plain':
        plain_text = request.data.decode('utf-8')
        lines = plain_text.splitlines()
        results = []
        for line in lines:
            predictions = predict(line)
            results.append({'text': line, 'predictions': predictions})
        output = io.StringIO()
        writer = csv.DictWriter(output, fieldnames=['text', 'predictions'])
        writer.writeheader()
        writer.writerows(results)
        return output.getvalue(), 200, {'Content-Type': 'text/csv'}
    else:
        return jsonify({'error': 'Unsupported content type'}), 415

