logging.basicConfig(level=logging.INFO)

app = Flask(__name__)

@app.route('/', methods=['POST'])
def handle_message():
    try:
        data = request.get_json()
        if not data or 'message' not in data:
            raise ValueError("Missing 'message' in payload")
        message = data['message']
        if 'attributes' not in message:
            raise ValueError("Missing 'attributes' in message")
        attributes = message['attributes']
        data_field = message.get('data', None)
        if data_field:
            try:
                decoded_data = base64.b64decode(data_field).decode('utf-8')
                data_dict = json.loads(decoded_data)
            except (base64.binascii.Error, json.JSONDecodeError) as e:
                logging.error(f"Error decoding data: {e}")
                data_dict = {}
        else:
            data_dict = {}

        payload = {
            'attributes': attributes,
            'data': data_dict
        }

        client = bigquery.Client()
        dataset_id = 'your_dataset'
        table_id = 'your_table'
        table_ref = client.dataset(dataset_id).table(table_id)
        table = client.get_table(table_ref)
        rows_to_insert = [payload]
        errors = client.insert_rows_json(table, rows_to_insert)
        if errors:
            for error in errors:
                logging.error(f"Error inserting row: {error}")

    except ValueError as ve:
        logging.error(f"Invalid payload: {ve}")
        return jsonify({"error": "Invalid payload"}), 400
    except Exception as e:
        logging.error(f"Error processing message: {e}")
        return jsonify({"error": "Internal server error"}), 500

    return '', 204

