            if date_str:
                try:
                    date_obj = datetime.strptime(date_str, "%Y-%m-%d").date()
                except ValueError:
                    return json({'error': 'Invalid date format'}, status=400)
            else:
                date_obj = None

            if date_obj:
                record = await ExchangeRates.query.where(ExchangeRates.date == date_obj).gino.first()
            else:
                record = await ExchangeRates.query.order_by(ExchangeRates.date.desc()).gino.first()

            if not record:
                return json({'error': 'No exchange rates found for the specified date'}, status=404)

            if symbols:
                filtered_rates = {k: v for k, v in record.rates.items() if k in symbols}
                if not filtered_rates:
                    return json({'error': 'No exchange rates found for the specified symbols'}, status=404)
            else:
                filtered_rates = record.rates

            if base != 'USD':
                inverted_rates = {}
                for symbol, rate in filtered_rates.items():
                    if symbol == base:
                        inverted_rates[symbol] = 1.0
                    else:
                        inverted_rates[symbol] = 1.0 / rate
                filtered_rates = inverted_rates

            return json({
                'base': base,
                'date': record.date.isoformat(),
                'rates': filtered_rates
            })

    def run(self, host="0.0.0.0", port=8000):
        self.app.run(host=host, port=port)

