    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words = defaultdict(list)
        file_path = os.path.join(self.model_dir, topic_words_file_name)
        if os.path.exists(file_path):
            with open(file_path, 'r') as f:
                for line in f:
                    parts = line.strip().split()
                    if len(parts) >= 3:
                        topic_id = int(parts[0])
                        word = parts[1]
                        score = float(parts[2])
                        topic_words[topic_id].append((word, score))
        return topic_words

    def _get_param(self, request, param_name: str, default_value=None, is_list: bool = False) -> Union[str, List[str]]:
        value = request.args.get(param_name, default_value)
        if value is None:
            value = request.form.get(param_name, default_value)
        if value is None:
            value = request.json.get(param_name, default_value)
        if is_list and isinstance(value, str):
            value = value.split(',')
        return value

    def _strip_to_none(self, text: str) -> Optional[str]:
        stripped = text.strip()
        return stripped if stripped else None

    def _response(self, success: bool = True, data=None, message=None):
        return json({
            'success': success,
            'data': data,
            'message': message
        })

    def _error_response(self, message: str = 'Invalid request'):
        return self._response(success=False, message=message)

    def _setup_routes(self):
        self.app.add_route(self.tokenize_handler, '/tokenize', methods=['POST'])
        self.app.add_route(self.infer_handler, '/infer', methods=['POST'])
        self.app.add_route(self.topics_handler, '/topics', methods=['GET'])

