            return self._error_response('Text is required')
        cleaned_text = self._strip_to_none(text)
        if not cleaned_text:
            return self._error_response('Invalid text')
        tokens = self.inference_engine_lda.tokenize(cleaned_text)
        return self._response(data=tokens)

    async def infer_handler(self, request):
        text = self._get_param(request, 'text')
        if not text:
            return self._error_response('Text is required')
        cleaned_text = self._strip_to_none(text)
        if not cleaned_text:
            return self._error_response('Invalid text')
        topic_distribution = self.inference_engine_lda.infer(cleaned_text)
        return self._response(data=topic_distribution)

    async def topics_handler(self, request):
        return self._response(data=self.lda_topic_words)

    def run(self):
        self.app.run(host='0.0.0.0', port=8000, workers=self.n_workers)

