import http.client
import urllib.parse
import json
import re
import ssl
import uuid

class JSONRPCExecutor:
    def __init__(self, command, url, jsonrpc_method, jsonrpc_params=None, status="^2\\d\\d$", result_validator=None, verify_tls=True, method="POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params or []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn):
        request_id = str(uuid.uuid4())
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": request_id
        }
        json_data = json.dumps(payload)
        headers = {"Content-Type": "application/json"}
        conn.request(self.method, self.url, body=json_data, headers=headers)

    def _validate_response(self, response):
        try:
            json_response = json.loads(response.read().decode('utf-8'))
        except json.JSONDecodeError:
            return False
        if json_response.get("jsonrpc") != "2.0":
            return False
        if "error" in json_response:
            return False
        if "result" not in json_response:
            return False
        if self.result_validator:
            valid, message = self.result_validator(json_response["result"])
            if not valid:
                return False
        return True

    def execute(self):
        parsed_url = urllib.parse.urlparse(self.url)
        host = parsed_url.hostname
        path = parsed_url.path or '/'
        port = parsed_url.port or (443 if parsed_url.scheme == 'https' else 80)
        if parsed_url.scheme == 'https':
            context = ssl.create_default_context()
            if not self.verify_tls:
                context.check_hostname = False
                context.verify_mode = ssl.CERT_NONE
            else:
                context.check_hostname = True
                context.verify_mode = ssl.CERT_REQUIRED
            conn = http.client.HTTPSConnection(host, port, context=context)
        else:
            conn = http.client.HTTPConnection(host, port)
        try:
            self._send_request(conn)
            response = conn.getresponse()
            if not re.fullmatch(self.status, str(response.status)):
                return False
            if not self._validate_response(response):
                return False
            return True
        except Exception:
            return False
        finally:
            conn.close()

