import urllib.parse
import json
import base64

class BaseProxy:
    def __init__(self, service_url=None, service_port=None, btc_conf_file=None, timeout=60, connection=None):
        self.__url = urllib.parse.urlparse(service_url)
        self.__service_url = service_url
        self.__id_count = 0
        self.__auth_header = None

        if connection:
            self.__conn = connection
        else:
            if self.__url.scheme == 'https':
                self.__conn = http.client.HTTPSConnection(self.__url.hostname, self.__url.port, timeout=timeout)
            else:
                self.__conn = http.client.HTTPConnection(self.__url.hostname, self.__url.port, timeout=timeout)

        if btc_conf_file:
            with open(btc_conf_file, 'r') as f:
                cred = f.read().strip()
                user, pwd = cred.split(':')
                self.__auth_header = base64.b64encode(f"{user}:{pwd}".encode('utf-8'))

    def _call(self, service_name, *args):
        self.__id_count += 1
        payload = {
            'jsonrpc': '2.0',
            'method': service_name,
            'params': list(args),
            'id': self.__id_count
        }
        headers = {
            'Content-Type': 'application/json'
        }
        if self.__auth_header:
            headers['Authorization'] = 'Basic ' + self.__auth_header.decode('utf-8')
        self.__conn.request('POST', self.__url.path, json.dumps(payload), headers)
        return self._get_response()

    def _get_response(self):
        response = self.__conn.getresponse()
        if response.status >= 400:
            raise Exception(f"HTTP error {response.status}: {response.reason}")
        data = response.read()
        try:
            json_data = json.loads(data.decode('utf-8'))
        except json.JSONDecodeError as e:
            raise Exception("Invalid JSON response") from e
        if 'error' in json_data:
            raise Exception(json_data['error']['message'])
        return json_data['result']

    def close(self):
        if hasattr(self, '__conn') and self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()

