    def recvLine(sock):
        data = ''
        while True:
            chunk = sock.recv(1024)
            if not chunk:
                return (False, '')
            data += chunk.decode('utf-8')
            if '\n' in data:
                line = data.split('\n', 1)[0]
                return (True, line)
        return (False, '')

    @staticmethod
    def recvAll(sock, l):
        data = b''
        while len(data) < l:
            chunk = sock.recv(l - len(data))
            if not chunk:
                return ''
        return data.decode('utf-8')

