class TCPForwardServer:
    def __init__(self, lhost: str, lport: int, rdev: str, rport: int) -> None:
        self.BUFFER_SIZE = 4096
        self.DELAY = 0.1
        self.input_list = []
        self.channel = {}
        self._server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._server.bind((lhost, lport))
        self._server.listen(5)
        self._server.setblocking(False)
        self.input_list.append(self._server)
        self._rdev = rdev
        self._rport = rport

    def main_loop(self):
        while True:
            readable, _, _ = select.select(self.input_list, [], [], self.DELAY)
            for sock in readable:
                if sock == self._server:
                    self.on_accept()
                elif sock in self.channel:
                    data = sock.recv(self.BUFFER_SIZE)
                    if data:
                        device_sock = self.channel[sock]
                        device_sock.send(data)
                elif sock in self.channel.values():
                    data = sock.recv(self.BUFFER_SIZE)
                    if data:
                        for client_sock, device_sock in self.channel.items():
                            if device_sock == sock:
                                client_sock.send(data)
                                break
                else:
                    self.on_close(sock)

    def on_accept(self):
        client_socket, addr = self._server.accept()
        client_socket.setblocking(False)
        self.input_list.append(client_socket)
        device_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        try:
            device_socket.connect((self._rdev, self._rport))
            device_socket.setblocking(False)
            self.channel[client_socket] = device_socket
            self.input_list.append(device_socket)
        except Exception as e:
            print(f"Connection to remote device failed: {e}")
            client_socket.close()
            self.input_list.remove(client_socket)
            device_socket.close()

    def on_close(self, sock):
        if sock in self.channel:
            client_sock, device_sock = sock, self.channel[sock]
            del self.channel[client_sock]
            self.input_list.remove(client_sock)
            self.input_list.remove(device_sock)
            sock.close()
            device_sock.close()
        else:
            self.input_list.remove(sock)
            sock.close()

