from PyQt5.QtCore import pyqtSignal

class ConnectionStatus(Enum):
    CONNECTED = 1
    CLOSED = 0

class UDPCommunication:
    def __init__(self):
        self.config = {
            'protocol': 'udp',
            'port': 5000,
            'target': ''
        }
        self.conn = None
        self.status = ConnectionStatus.CLOSED
        self.onConnectionStatus = pyqtSignal()
        self.hintSignal = pyqtSignal(str)
        self.receiveProcess = None
        self.running = False

    def openCloseSerial(self):
        if self.status == ConnectionStatus.CLOSED:
            try:
                self.conn = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
                self.conn.bind(('', self.config['port']))
                self.running = True
                self.receiveProcess = threading.Thread(target=self.receiveDataProcess)
                self.receiveProcess.start()
                self.status = ConnectionStatus.CONNECTED
                self.onConnectionStatus.emit()
            except Exception as e:
                self.hintSignal.emit(f"Error opening connection: {str(e)}")
                self.status = ConnectionStatus.CLOSED
        else:
            try:
                self.conn.close()
                self.running = False
                self.receiveProcess.join()
                self.conn = None
                self.status = ConnectionStatus.CLOSED
                self.onConnectionStatus.emit()
            except Exception as e:
                self.hintSignal.emit(f"Error closing connection: {str(e)}")
                self.status = ConnectionStatus.CLOSED

    def receiveDataProcess(self):
        while self.running:
            try:
                data, addr = self.conn.recvfrom(65535)
                print(f"Received data: {data} from {addr}")
            except Exception as e:
                self.hintSignal.emit(f"Error receiving data: {str(e)}")
                break
        self.running = False

    def send(self, data):
        if self.status == ConnectionStatus.CONNECTED:
            try:
                target = self.checkTarget(self.config['target'])
                self.conn.sendto(data, target)
            except Exception as e:
                self.hintSignal.emit(f"Error sending data: {str(e)}")
        else:
            self.hintSignal.emit("Connection is closed. Cannot send data.")

    def checkTarget(self, target):
        if ':' in target:
            host, port_str = target.split(':', 1)
            try:
                port = int(port_str)
                if 0 <= port <= 65535:
                    return (host, port)
                else:
                    raise ValueError("Port out of range")
            except ValueError as e:
                raise ValueError(f"Invalid port: {str(e)}")
        else:
            raise ValueError("Target must contain a host and port separated by colon")

    def isConnected(self):
        return self.status == ConnectionStatus.CONNECTED

    def getConnStatus(self):
